import React, { useState, useEffect } from "react"
import MaterialCard from "./MaterialCard.js"
import axios from 'axios';

function Home(){

    const [materials, setMaterial] = useState([]);
    const [isButtonClicked, setIsButtonClicked] = useState(false);

    useEffect(() => {
        axios.get("/api/materials")
            .then(res => setMaterial(res.data))
            .catch(err => {
                console.log(err)
        })
    }, [isButtonClicked])

    const handleClick = () => {   
        setIsButtonClicked(true)
    }


    const materialsArray = materials.map(material =>
        <MaterialCard imgUrl={material.imgUrl} sloganText={material.sloganText} 
        />)

    return(
        <div className="home">
            <h2>Home</h2>
            <button type="button" onclick={handleClick}> Generate </button>
            {materialsArray}
        </div>
    )
}

 

export default Home
