import React, { useState, useEffect } from "react"
import MaterialCard from "./MaterialCard.js"
import axios from 'axios';




function Home(){

    const [materials, setProduct] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            const {data} = await axios.get("/api/materials");
            setProduct(data);
        } 
        fetchData();
        return () => {       
        };
    }, [])

    const materialsArray = materials.map(material =>
        <MaterialCard imgUrl={material.imgUrl} sloganText={material.sloganText} 
        />)

    return(
        <div className="home">
            <h2>Home</h2>
            {materialsArray}
        </div>
    )
}

export default Home
