import React, { useState, useEffect } from "react"
import MaterialCard from "./MaterialCard.js"
import axios from 'axios';
import "./Home.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {faHandPointDown} from '@fortawesome/free-solid-svg-icons';


function Home(){

    const [materials, setMaterial] = useState([]);

    const handleClick = () => {   
        axios.get("/materials")
            .then(res => {
                console.log(res);
                setMaterial(res.data.materials);
            })
            .catch(err => {
                console.log(err);
        })
    }


    const materialsArray = materials.map(material =>
        <MaterialCard imgUrl={material.imageFile} sloganText={material.sloganText} likes={material.likes} dislikes={material.dislikes}
        />)

    return(
        <div className="home">
            <div className="block-content">
                <h1>AIContentCreator.org</h1>
                <h3>An A.I-enabled content creation engine to offer inspirations for artists and digital marketing professionals. <br /><br /><hr /></h3> 
            </div>
            <h4> <FontAwesomeIcon icon={faHandPointDown} /> Click the button below to check out our A.I.-generated digital marketing materials! <br /> </h4>
            <p>
                <button type="button" class="generate-button" onClick={handleClick}> Generate Materials </button>
            </p>
            {materialsArray}
        </div>
    )
}

 

export default Home
