import React, { useState, useEffect } from "react"
import MaterialCard from "./MaterialCard.js"
import axios from 'axios';

function Home(){

    const [materials, setMaterial] = useState([]);

    const handleClick = () => {   
        axios.get("/api/materials")
            .then(res => setMaterial(res.data))
            .catch(err => {
                console.log(err)
        })
    }


    const materialsArray = materials.map(material =>
        <MaterialCard imgUrl={material.imgUrl} sloganText={material.sloganText} 
        />)

    return(
        <div className="home">
            <h2>Home</h2>
            <div>
                <button type="button" class="generate-button" onClick={handleClick}> Generate Materials </button>
            </div>
            {materialsArray}
        </div>
    )
}

 

export default Home
